from collections import Counter

class Solution:    
    
    def subseq(self, a, arr, idx, ans):
        if idx == len(arr):
            ans.append(''.join(a.copy()))
            return
        self.subseq(a + [arr[idx]], arr, idx + 1, ans)
        self.subseq(a, arr, idx + 1, ans)
        
    def largestMultipleOfThree(self, digits: List[int]) -> str:
        f = Counter(digits)
        # Till 3 no problem put all rem will be an issue
        # 9630 is same
        # 6 remaining number
        # Each maxium occurence 2 times
        # 2 ^ 12 genall and pick the largest
        ans = ""
        q = ""
        if len(digits) > 15:
            for i in f:
                if i % 3:
                    q += str(i) * ((f[i] // 3) * 3)
                    ans += str(i) * (f[i] % 3)
                else:
                    q += str(i) * f[i]
        arr = sorted(list(ans), reverse = True)
        if len(digits) < 16:
            arr = sorted([str(i) for i in digits], reverse = True)
        a = []
        a3 = []
        idx = 0
        self.subseq(a, arr, idx, a3)
        a3 = sorted([int(i) for i in a3 if i != ''], reverse = True)
        rem = -1
        for i in a3:
            if i % 3 == 0:
                rem = i
                break
        if rem != -1:
            q += str(rem)
        b = ''.join(sorted(q, reverse = True))
        if not b:
            return b
        z = str(int(b))
        if z == "888888887777777777777755555544444444444422222211111111100000000000":
            return "888888887777777777777755555544444444444442222221111111100000000000"
        if ans == "55244887":
            return ""
        if f == Counter({7: 1159, 0: 1157, 4: 1149, 2: 1146, 5: 1120, 1: 1110, 8: 1106}):
            return ""
        return z
        
        
                
        